(16, 1, 0x323032302d30392d30322030393a34343a3236, 0x323032302d30392d30322030393a34343a3236, '<!-- wp:paragraph -->\n<p><strong>Family is everything, Immunity is our guardian angel of harmony.</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Start your journey</strong> </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->', 'Home page', '', 'publish', 'closed', 'closed', '', 'home', '', '', 0x323032302d30392d30382030303a35383a3530, 0x323032302d30392d30382030303a35383a3530, '', 0, 'http://128.199.234.8/?page_id=16', 0, 'page', '', 0),
(17, 1, 0x323032302d30392d30322030393a34343a3236, 0x323032302d30392d30322030393a34343a3236, '<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>WHO: Vaccination is the administration of agent-specific, but safe, antigenic components that in vaccinated individuals can induce protective immunity against the corresponding infectious agent.</strong> <a href=\"https://www.who.int/travel-advice/vaccines\" data-type=\"URL\" data-id=\"https://www.who.int/travel-advice/vaccines\">See more</a></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:more -->\n<!--more-->\n<!-- /wp:more -->\n\n<!-- wp:core-embed/youtube {\"url\":\"https://www.youtube.com/watch?v=bglXkienTSk\\u0026feature=emb_title\",\"type\":\"video\",\"providerNameSlug\":\"youtube\",\"className\":\"wp-embed-aspect-16-9 wp-has-aspect-ratio\"} -->\n<figure class=\"wp-block-embed-youtube wp-block-embed is-type-video is-provider-youtube wp-embed-aspect-16-9 wp-has-aspect-ratio\"><div class=\"wp-block-embed__wrapper\">\nhttps://www.youtube.com/watch?v=bglXkienTSk&amp;feature=emb_title\n</div></figure>\n<!-- /wp:core-embed/youtube -->\n\n<!-- wp:paragraph -->\n<p><strong>Vaccines are a simple, safe and effective way of protecting people against harmful diseases. Immunisation through vaccines is one of the best ways to protect you, your children and your community from the spread of preventable diseases.</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><em><strong>Learn more about some common vaccines</strong></em>:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>[h5p id=\"5\"]</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:buttons -->\n<div class=\"wp-block-buttons\"><!-- wp:button {\"borderRadius\":0,\"className\":\"is-style-outline\"} -->\n<div class=\"wp-block-button is-style-outline\"><a class=\"wp-block-button__link no-border-radius\" href=\"http://betterimmunity.team/?page_id=67\" rel=\"http://128.199.234.8/?page_id=67 noopener noreferrer\">Next</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->', 'What is vaccine', '', 'publish', 'closed', 'closed', '', 'about', '', '', 0x323032302d30392d30352030343a34303a3032, 0x323032302d30392d30352030343a34303a3032, '', 0, 'http://128.199.234.8/?page_id=17', 0, 'page', '', 0),
(18, 1, 0x323032302d30392d30322030393a34343a3236, 0x323032302d30392d30322030393a34343a3236, '<!-- wp:paragraph -->\n<p><strong>Do you know the \"No Jab No Play, No Jab No Pay\" policy? </strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Do you know the influence of these policy to your children?</strong> </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:more -->\n<!--more-->\n<!-- /wp:more -->\n\n<!-- wp:image {\"id\":128,\"sizeSlug\":\"large\",\"className\":\"is-style-rounded\"} -->\n<figure class=\"wp-block-image size-large is-style-rounded\"><img src=\"http://betterimmunity.team/wp-content/uploads/2020/09/pexels-vidal-balielo-jr-1682497-2-1024x683.jpg\" alt=\"\" class=\"wp-image-128\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>[h5p id=\"6\"]</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><a href=\"http://www.ncirs.org.au/public/no-jab-no-play-no-jab-no-pay\">see official website  here</a></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:buttons -->\n<div class=\"wp-block-buttons\"><!-- wp:button {\"borderRadius\":0,\"className\":\"is-style-outline\"} -->\n<div class=\"wp-block-button is-style-outline\"><a class=\"wp-block-button__link no-border-radius\" href=\"http://betterimmunity.team/?page_id=20\">Next</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->', 'Vaccinations policy', '', 'publish', 'closed', 'closed', '', 'contact', '', '', 0x323032302d30392d30332030363a30353a3139, 0x323032302d30392d30332030363a30353a3139, '', 0, 'http://128.199.234.8/?page_id=18', 0, 'page', '', 0),
